import React, { useState, useEffect } from "react"
import styles from "../scss/work.module.scss"
import { Link } from "react-router-dom"

const Work = props => {
  const [openAnimate, setOpenAnimate] = useState(false)
  const [openfromTab, setOpenfromTab] = useState(false)
  const [contentHeight, setContentHeight] = useState(0)
  useEffect(() => {
    if (props.location.state) {
      setTimeout(() => setOpenAnimate(true), 300)
    } else {
      setOpenfromTab(true)
    }
  }, [])

  useEffect(() => {
    //nav 140px + 100 margin top
    console.log(window.innerHeight - 240)
    setContentHeight(window.innerHeight - 240 - 18)
  }, contentHeight)

  const openReact = () => {
    console.log("open react")
  }
  return (
    <div>
      {openfromTab ? null : (
        <label
          className={
            openAnimate
              ? styles.work
              : [styles.startWork, styles.work].join(" ")
          }
        />
      )}
      <div
        className={styles.workContainer}
        style={{ height: contentHeight + "px" }}
      >
        <div className={styles.react}>
          <Link to="/Work/oz360">
            <div className={styles.reactHover} onClick={openReact}>
              <div className={styles.reactContent}>
                <div className={styles.logoImage} />

                <div className={styles.title}>React Website Application</div>
                <div>
                  360OZ : Improve the mental health of international students{" "}
                </div>
                <div className={styles.arrow} />
              </div>
            </div>
          </Link>
        </div>
        <div className={styles.beer}>beer</div>
        <div className={styles.merklise}>merklise</div>
        <div className={styles.time}>time</div>
        <div className={styles.smarter}>smarter</div>
        <div className={styles.visualization}>visualization</div>
        <div className={styles.beetle}>beetle</div>
        <div className={styles.solidworks}>solidworks</div>
      </div>
    </div>
  )
}

export default Work
